UPDATE recipes
SET translations = '{"en-US":{"name":"Pudim de banana com tapioca","ingredients":["Ingredientes da syrup","300g of sugar refinado","180ml of água","Ingredientes do pudim","200ml of água","60g of creme of leite ou leite of coco","70g of tapioca granulada","Demais ingredientes","120g of banana nanica","60g of farinha hipoproteica","150ml of água","60g of creme of leite ou leite of coco","1g of essência of vanilla","0,5g of canela em pó"],"preparation":["Modo of preparo da syrup","1 - Em uma pan, derreta o sugar em low heat até caramelizar;","2 - Com cuidado, adicione a água aos poucos e mix até desmanchar os grumos of caramel;","3 - É possível fazer a syrup antecipadamente e deixar na refrigerator para que fique mais consistente.","Modo of preparo do pudim","1 - Aqueça a água com creme of leite ou leite of coco e jogue por cima da tapioca;","2 - mix bem e deixe hidratar em refrigerator por aproximadamente 2 horas até que fique inchada e macia;","3 - No blender, beat a tapioca já hidratada com o restante dos ingredientes por cerca of 2 minutos;","4 - Espalhe a syrup no fundo e laterais of uma forma e despeje a massa;","5 - Asse em forno pré-aquecido a 200°C por aproximadamente 45 minutos;","6 - Retire do forno e deixe esfriar antes of desenformar;","7 - Aqueça o fundo da forma na chama do fogão e desenforme com ajuda of uma faca soltando as laterais;","8 - Espalhe a syrup por cima e sirva bem gelado."],"servings":"20 unidades"}}'::jsonb
WHERE id = 40;

UPDATE recipes
SET translations = '{"en-US":{"name":"Pudim de tapioca","ingredients":["100ml of água","160g of sugar refinado","200g of leite of coco diluído em 100ml of água","100g of tapioca desidratada","25g of margarina sem sal","30g of amido of milho","125g of creme of leite","10g of coco ralado","10g of pó para preparo of flan sabor vanilla"],"preparation":["Modo of preparo da syrup","1 - Em uma pan, derreta o quanto baste of sugar e faça um caramel;","2 - Envolva uma forma com a syrup of caramel ainda quente e reserve.","Modo of preparo do pudim","1 - Em uma vasilha, coloque a tapioca of molho no leite of coco diluído em água por alguns minutos;","2 - beat no blender a tapioca já hidratada com os demais ingredientes, exceto o coco ralado;","3 - Adicione o coco na mistura e coloque na forma caramelizada;","4 - Asse em banho maria no forno ou pan por aproximadamente 40 minutos."],"servings":"10 fatias"}}'::jsonb
