UPDATE recipes
SET translations = '{"en-US":{"name":"Tapioca Pudding","ingredients":["100ml of water","160g of refined sugar","200g of coconut milk diluted in 100ml of water","100g of dehydrated tapioca","25g of unsalted margarine","30g of cornstarch","125g of cream of milk","10g of grated coconut","10g of vanilla flan mix"],"preparation":["Method of preparing the syrup","1 - In a pan, melt the sugar and make a caramel;","2 - Coat a mold with the still hot caramel syrup and set aside.","Method of preparing the pudding","1 - In a bowl, soak the tapioca in the coconut milk diluted in water for a few minutes;","2 - beat the already hydrated tapioca with the other ingredients in a blender, except the grated coconut;","3 - Add the coconut to the mixture and place it in the caramelized mold;","4 - Bake in a water bath in the oven or pan for approximately 40 minutes."],"servings":"10 slices"}}'::jsonb
WHERE id = 41;

UPDATE recipes
SET translations = '{"en-US":{"name":"Cassava Coxinha","ingredients":["500ml of water","400g of cassava","100g of low protein flour","70g of cassava flour","25g of unsalted margarine","6g of salt","1g of sweet paprika","1g of dried oregano"],"preparation":["1 - Cook the cassava, mash until you get a puree and set aside;","2 - In a pan, boil the water with the margarine, salt, paprika and oregano;","3 - Add the cassava puree and dissolve well until it becomes a homogeneous mass;","4 - Add the low protein flour and cook for 5 minutes, stirring constantly;","5 - Remove the dough from the pan and place it on a counter, knead with the help of a spoon and then with your hands until it is smooth and firm. Let it cool to mold;","6 - Fill with cooked and seasoned vegetables to taste;","7 - To bread, pass in water and then in sifted cassava flour;","8 - Fry in hot oil and place on absorbent paper."],"servings":"18 units"}}'::jsonb
WHERE id = 42;

UPDATE recipes
SET translations = '{"en-US":{"name":"Pie or Empada Dough","ingredients":["100g of low protein flour","70g of unsalted margarine","5g of emulsifier","3g of salt","1g of chemical baking powder","0,5g of turmeric powder"],"preparation":["1 - Mix all ingredients, except the flour;","2 - Add the flour little by little until the dough is ready, which should be similar to pastry dough;","3 - Place in small molds and fill with vegetables to taste;","4 - Bake in a preheated oven at 200°C until golden."],"servings":"6 units"}}'::jsonb
WHERE id = 43;

UPDATE recipes
SET translations = '{"en-US":{"name":"Empadinha Dough","ingredients":["140g of gluten-free flour","1g of xanthan gum","2g of salt","2g of refined sugar","60g of unsalted margarine","40ml of water"],"preparation":["1 - In a container, mix all ingredients until you get a smooth dough that doesn''t stick to your hands;","2 - Roll out the dough with the help of a rolling pin and place it in individual molds;","3 - Fill with the filling of your choice;","4 - Bake in a preheated oven at 180°C for approximately 20 minutes."],"servings":"5 units"}}'::jsonb
WHERE id = 44;

UPDATE recipes
SET translations = '{"en-US":{"name":"Sweet Pancake","ingredients":["40ml of warm water","20g of cream of milk","50g of gluten-free flour","4g of vegetable oil","5g of chemical baking powder","30g of refined sugar"],"preparation":["1 - In a container, mix all ingredients and beat with the help of a whisk or mixer until you get a homogeneous dough;","2 - Take portions of the dough with a ladle and place them in a preheated and oiled pan;","3 - Brown the dough on both sides","4 - Fill to taste."],"servings":"2 units"}}'::jsonb
WHERE id = 45;

UPDATE recipes
SET translations = '{"en-US":{"name":"Carrot Pancake","ingredients":["200ml of water","15g of cream of milk","6g of vegetable oil","2g of salt","3g of chemical baking powder","20g of carrot","70g of gluten-free flour"],"preparation":["1 - Beat all ingredients in a blender and set aside;","2 - Grease a pan with a drizzle of oil and heat until warm;","3 - Divide the dough into 3 equal portions and with the help of a ladle take a portion of dough and place it in the pan, turn the pan to spread the dough well;","4 - Lower the heat and let it brown underneath, then turn it over and let it brown;","5 - Repeat the process with all the dough;","6 - Fill to taste."],"servings":"3 units"}}'::jsonb
WHERE id = 46;

UPDATE recipes
SET translations = '{"en-US":{"name":"Pastel Dough","ingredients":["220g of cassava","120g of gluten-free flour","20g of cream of milk","3g of salt"],"preparation":["1 - Cook the cassava and pass it through a sieve or mash it well with a fork while still hot;","2 - In a container, place the cassava puree, add the cream of milk, salt and gradually add the flour until you get a homogeneous dough;","3 - Roll out the dough with the help of a rolling pin and shape the pastels;","4 - Place the filling of your choice;","5 - Bake in a preheated oven for approximately 20 minutes."],"servings":"10 units"}}'::jsonb
WHERE id = 47;

UPDATE recipes
SET translations = '{"en-US":{"name":"Catarina Pastel","ingredients":["30ml of warm water","40g of cream of milk","30g of mayonnaise","3g of mustard","6g of apple cider vinegar","2g of salt","150g of gluten-free flour"],"preparation":["1 - In a container, mix all ingredients, except the flour;","2 - Mix well and gradually add the flour until the dough comes off your hands;","3 - Roll out the dough with the help of a rolling pin and cut into circles;","4 - Fill to taste and fold in half, closing the sides well;","5 - Bake or fry until crispy."],"servings":"5 units"}}'::jsonb
WHERE id = 48;

UPDATE recipes
SET translations = '{"en-US":{"name":"Pizza Dough","ingredients":["90g of low-protein flour","90g of corn starch","50ml of warm water","30g of cream of milk","30g of mayonnaise","4g of chemical baking powder","3g of salt"],"preparation":["1 - In a container, place the flour, corn starch and baking powder and set aside;","2 - Mix the cream of milk with the water and pour into the dry mixture;","3 - Mix with a spoon until it reaches the point of kneading with your hands;","4 - Roll out the dough and divide it into two portions in the shape of a pizza;","5 - Add the desired filling and bake in a preheated oven at 200°C for approximately 25 minutes."],"servings":"2 units"}}'::jsonb
WHERE id = 49;

UPDATE recipes
SET translations = '{"en-US":{"name":"Spinach Ravioli","ingredients":["Filling ingredients","40g of mandioquinha","10g of unsalted margarine","2g of salt","1g of parsley or chives","Dough ingredients","150g of gluten-free flour 100ml of water","7g of lard","10g of raw spinach","5g of CMC or xanthan gum","2g of salt"],"preparation":["Filling preparation method","1 - Peel and cook the mandioquinha until soft;","2 - Mash until it becomes a puree and add the other ingredients, set aside.","Dough preparation method","1 - Wash the spinach leaves;","2 - Blend with the minimum amount of water possible;","3 - In a bowl, place the flour, CMC or xanthan gum, salt and lard;","4 - Make a crumble and gradually add the contents of the blender until you get a smooth and homogeneous dough.","Assembly","1 - Roll out the dough with the help of a rolling pin;","2 - Place the filling, fold the dough and cut into the shape of your choice;","3 - Cook in boiling water with a drizzle of oil;","4 - Remove from water when they start to float;","5 - Serve with the sauce of your choice."],"servings":"8 units"}}'::jsonb
WHERE id = 50;

UPDATE recipes
SET translations = '{"en-US":{"name":"Risoles","ingredients":["Filling ingredients","25g of palm hearts","30g of champignon mushrooms 60g of tomato","15g of onion","10g of corn","3g of parsley","0,5g of dried oregano","5g of olive oil","Dough ingredients","200ml of water","7,5g of vegetable broth in sachet 30g of unsalted margarine","2g of xanthan gum or neutral binder 100g of low-protein flour","20g of gluten-free flour","For breading: 20g of cassava flour"],"preparation":["Dough preparation method","1 - In a pan, place the water with salt, oregano and margarine to boil;","2 - When it boils, gradually add the flours, stir and cook for a few minutes. The dough should have the consistency of polenta;","3 - Remove the dough still hot from the pan and knead with the help of a spoon, then with your hands, until you get a smooth dough to mold;","4 - Roll out the dough and cut into 10 circles to make the risoles, set aside.","Filling preparation method","1 - In a pan, sauté the onion in olive oil;","2 - Add the other ingredients and cook;","3 - Divide the filling into 10 equal portions and set aside.","Assembly","1 - Fill the dough and close the risoles by pressing the edges well;","2 - Pass the finished risoles in water and then in sifted cassava flour to bread;","3 - Fry quickly in hot oil and place on absorbent paper."],"servings":"10 units"}}'::jsonb
WHERE id = 51;

UPDATE recipes
SET translations = '{"en-US":{"name":"Waffles","ingredients":["180ml of warm water","50g of refined sugar","50g of mashed banana","100g of gluten-free flour","5g of sunflower oil","20g of cream of milk","5g of emulsifier","8g of apple cider vinegar","0,5g of vanilla essence","2g of chemical baking powder","1g of salt","1g of xanthan gum","1g of agar-agar"],"preparation":["1 - mix the mashed banana with the sugar and the emulsifier, reserve;","2 - mix the vinegar and the other ingredients;","3 - Add the flour and the chemical baking powder, beat everything with the help of a whisk;","4 - Turn on the waffle grill to the maximum temperature and wait for it to heat up;","5 - Place a portion of dough in the center of the grill and bake for 15 minutes or until golden and cooked. Do not open before 10 minutes;","6 - Serve with margarine, honey or jam."],"servings":"2 units"}}'::jsonb
WHERE id = 52;

UPDATE recipes
SET translations = '{"en-US":{"name":"Fine herbs bread rolls","ingredients":["130g of hydrated tapioca","250g of cooked potato","5g of vegetable oil","6g of salt","3g of chemical baking powder","15ml of water","0,5g of dried herbs (oregano, rosemary)"],"preparation":["1 - Mash the potato until it becomes a puree;","2 - In a bowl, mix the puree with the other ingredients;","3 - Knead until you get a moldable dough;","4 - Roll into balls with your hands or in the shape you desire;","5 - Place on a greased baking sheet;","6 - Bake in a preheated oven at 200° C for approximately 40 minutes."],"servings":"20 units"}}'::jsonb
WHERE id = 53;

UPDATE recipes
SET translations = '{"en-US":{"name":" Bread with flour mix","ingredients":["200g of corn starch","200g of sweet tapioca starch","100g of potato starch","100g of rice flour","50g of neutral binder","200ml of water","60g of refined sugar","10g of dry biological yeast powder 10g of salt","20g of emulsifier","90g of unsalted margarine","75g of cream of milk","15g of vegetable oil"],"preparation":["1 - In a container, place 100ml of hot water, the sugar, the margarine, the oil and the emulsifier. Mix well until the margarine melts;","2 - Add the remaining cold water and the yeast, let it rest for 5 minutes;","3 - Add the mixture to the flours, the salt, the cream of milk and beat in the mixer for a few minutes;","4 - Place the dough in a container, let it rise and beat again;","5 - Divide the dough into two greased bread pans and let it rise again;","6 - Bake in a preheated oven at 220°C until golden."],"servings":"21 slices"}}'::jsonb
WHERE id = 54;

UPDATE recipes
SET translations = '{"en-US":{"name":"Gourmet kiss bread","ingredients":["150g of cooked pumpkin","240g of cooked potato","90g of sweet tapioca starch","200g of sour tapioca starch","10g of chemical baking powder 70ml of water","30g of vegetable oil 5g of salt","2g of mashed garlic 5g of parsley"],"preparation":["1 - Mix the potato with the pumpkin and mash until you get a puree;","2 - Add the water and then the other ingredients, leaving the yeast for last;","3 - Mix everything until you get a smooth dough and can knead it with your hands;","4 - Make balls and place them on a baking sheet;","5 - Bake in a preheated oven at 180°C for approximately 1 hour."],"servings":"40 units"}}'::jsonb
WHERE id = 55;

UPDATE recipes
SET translations = '{"en-US":{"name":"French bread","ingredients":["320g of gluten-free flour","50g of sweet tapioca starch","9g of salt","30g of refined or brown sugar 10g of dry biological yeast powder","5g of neutral binder or xanthan gum 6g of olive oil","6g of apple cider vinegar","240ml of water","0,1g of turmeric powder"],"preparation":["1 - In a container, mix the vinegar, the oil and the water;","2 - Add the other ingredients, mix with a spoon and then with your hands;","3 - Knead well, the dough will be a bit sticky;","4 - Divide the dough into 8 equal parts and shape the breads with your hands greased with oil, it is not necessary to let the dough rise;","5 - Place on a baking sheet greased with margarine and make a cut from one end to the other on top of each of the breads;","6 - Bake in a preheated oven at 220°C for 25 to 30 minutes. Do not exceed the cooking time so that it does not become hard, the breads will be white;","7 - It is possible to freeze after they are ready and heat them in the microwave for 30 seconds."],"servings":"8 units"}}'::jsonb
WHERE id = 56;

UPDATE recipes
SET translations = '{"en-US":{"name":"Cheese flavor bread","ingredients":["130g of sour tapioca starch","60g of sweet tapioca starch","90g of grated vegan cheese 220g of cream of milk","5g of salt"],"preparation":["1 - In a bowl, mix all the ingredients except the sweet tapioca starch;","2 - Add the sweet tapioca starch and mix again, the dough consistency is sticky;","3 - It is possible to use only the sour tapioca starch, if you prefer;","4 - Shape the breads with the help of two spoons;","5 - Place the portions on a baking sheet greased with margarine;","6 - Bake in a preheated oven at 200°C until fully baked."],"servings":"16 units"}}'::jsonb
WHERE id = 57;

UPDATE recipes
SET translations = '{"en-US":{"name":"Skillet bread","ingredients":["60g of gluten-free flour","15g of refined sugar","6g of vegetable oil","30g of mashed banana","0,5g of xanthan gum or neutral binder 35ml of warm water","25g of cream of milk","8g of apple cider vinegar","1g of chemical baking powder 1g of salt"],"preparation":["1 - In a container, mix all the ingredients except the water.","2 - Add the water little by little until you get a homogeneous mixture;","3 - In a greased non-stick skillet, place half of the dough, cover and brown on both sides;","4 - Repeat the process with the other half of the dough."],"servings":"2 units"}}'::jsonb
WHERE id = 58;

UPDATE recipes
SET translations = '{"en-US":{"name":"Yam bread rolls","ingredients":["270g of cooked yam","100g of sweet tapioca starch","100g of sour tapioca starch","30g of sunflower oil","7g of salt","1g of dried oregano"],"preparation":["1 - Cook the yam and mash until it becomes a puree;","2 - In a container, place the yam puree and add the other ingredients;","3 - Mix everything and knead with your hands until it becomes a smooth and homogeneous dough;","4 - If the dough becomes dry, add a little hot water to reach the right consistency;","5 - Make balls and place them on a baking sheet;","6 - Bake in a preheated oven at 200°C until the bottom is golden."],"servings":"30 units"}}'::jsonb
WHERE id = 59;

UPDATE recipes
SET translations = '{"en-US":{"name":"Cassava bread","ingredients":["600g of cooked cassava","300g of sour tapioca starch","200g of sweet tapioca starch","150ml of olive oil or vegetable oil","5g of salt","175ml of warm water"],"preparation":["1 - In a container, mash the potato while still hot until it becomes a puree;","2 - Add the other ingredients and finally the water;","3 - Knead well until it becomes homogeneous;","4 - Make balls and bake in a preheated oven at 180°C until golden."],"servings":"23 units"}}'::jsonb
WHERE id = 60;

UPDATE recipes
SET translations = '{"en-US":{"name":"Cassava bread rolls","ingredients":["400g of cassava","400g of sweet tapioca starch","100g of sour tapioca starch","250ml of warm water"],"preparation":["1 - Cook the cassava and mash until it becomes a puree;","2 - Add the tapioca starches and add the water little by little;","3 - Mix well and then knead with your hands until it forms a firm and homogeneous dough;","4 - Make balls and place them on a baking sheet;","5 - Bake in a preheated oven at 200°C until it rises like conventional cheese bread and browns lightly."],"servings":"30 units"}}'::jsonb
WHERE id = 61;

UPDATE recipes
SET translations = '{"en-US":{"name":"Kiss bread rolls","ingredients":["200g of sweet tapioca starch","50g of sour tapioca starch","360g of cooked cassava","100ml of water","40g of vegetable oil","5g of chemical baking powder","4g of salt","0,5g of dried oregano"],"preparation":["1 - Mash the cooked cassava while still hot until it becomes a puree and set aside;","2 - In a pan, boil the water, oil, and salt;","3 - In another container, mix the tapioca starches and add the seasoned water;","4 - Mix well and add the cassava puree little by little, until you get a smooth dough that doesn''t stick to your hands;","5 - Make balls and place them on a greased baking sheet;","6 - Bake in a preheated oven at 180°C until golden."],"servings":"20 units"}}'::jsonb
WHERE id = 62;

UPDATE recipes
SET translations = '{"en-US":{"name":"Gluten-free bread","ingredients":["300g of gluten-free flour","65g of organic sugar","7g of dry biological yeast","5g of emulsifier","2g of neutral binder","75g of unsalted margarine","60g of creme of leite","190ml of water","3g of salt"],"preparation":["1 - In a container, mix well the flour, sugar, emulsifier, neutral binder and yeast, set aside;","2 - Heat the margarine together with the creme of leite in the microwave for 25 seconds;","3 - Add this mixture to the previous dough and mix with the help of a whisk;","4 - Add the warm water little by little until the dough is ready to knead, not too soft;","5 - Place the dough in the mixer and beat at minimum speed for 5 minutes;","6 - Arrange the dough in a loaf pan and let it rise for 20 minutes;","7 - Bake in a preheated oven at 230°C for approximately 35 minutes or until fully baked."],"servings":"14 slices"}}'::jsonb
WHERE id = 63;

UPDATE recipes
SET translations = '{"en-US":{"name":"Crispy zucchini","ingredients":["200g of zucchini","40g of cornstarch","1g of dried oregano","3g of salt","Oil for frying"],"preparation":["1 - Wash and cut the zucchini into slices, set aside;","2 - In a container, mix the cornstarch with the salt and oregano;","3 - Pass the zucchini slices through this mixture;","4 - Fry in hot oil and place on absorbent paper."],"servings":"2 servings"}}'::jsonb
WHERE id = 64;

UPDATE recipes
SET translations = '{"en-US":{"name":"Eggplant meatballs","ingredients":["200g of eggplant without skin","30g of onion","10g of garlic","50g of cassava flour","5g of parsley","10g of olive oil","4g of salt","0,5g of dried oregano"],"preparation":["1 - cut the eggplant into very small pieces and let it soak in water;","2 - In a pan, sauté the garlic and onion in olive oil;","3 - Drain the eggplant and add it to the pan, season with salt;","4 - Cover the pan and lower the heat, the eggplant will release water that will be necessary for cooking, stir occasionally until the water dries up and the eggplant is tender;","5 - If necessary, add a little more water;","6 - Add the parsley and turn off the heat;","7 - Let it cool and mash well with a fork until it is well ground;","8 - Add the cassava flour until it is in the right consistency to be shaped into balls;","9 - Arrange in a baking sheet and bake in a preheated oven until golden."],"servings":"10 units"}}'::jsonb
WHERE id = 65;

UPDATE recipes
SET translations = '{"en-US":{"name":"Stuffed potato","ingredients":["490g of large potatoes","5g of mashed garlic","3g of salt","50g of vegan cheese","60g of cream of leite","40g of mayonnaise","1g of oregano"],"preparation":["1 - Wash the potatoes, cut the two ends so that the potatoes stand upright and cut them in half or lengthwise, if you prefer;","2 - In a pressure cooker, cook for about 10 minutes after it starts to hiss;","3 - With the potatoes still hot, remove the center preserving the bottom and sides;","4 - Mash the portion removed from the potatoes and season with margarine, garlic, cream of leite, salt and herbs to taste;","5 - Arrange the potatoes in a baking dish, sprinkle with salt and place the seasoned puree inside the potatoes;","6 - Separately, mix the cheese gratin ingredients and place them on top of the potatoes;","7 - Bake in a preheated oven at 200°C until golden."],"servings":"8 units"}}'::jsonb
WHERE id = 66;

UPDATE recipes
SET translations = '{"en-US":{"name":"Baked potatoes","ingredients":["200g of potato","90g of cherry tomato","15g of olive oil","8g of garlic","1g of rosemary or dried oregano","0,5g of hot paprika","2g of salt"],"preparation":["1 - In a pan with water and salt, pre-cook the potatoes already cut;","2 - Arrange the potatoes in a baking dish and add the tomatoes, garlic, rosemary and paprika;","3 - Bake in a preheated oven at 200°C until golden."],"servings":"2 servings"}}'::jsonb
WHERE id = 67;

UPDATE recipes
SET translations = '{"en-US":{"name":"Breaded eggplant","ingredients":["300g of eggplant","70g of cornstarch","5g of salt","2g of dried oregano or chimichurri","Oil for frying"],"preparation":["1 - Chop the eggplant and let it soak in water with vinegar;","2 - Remove from water and season the eggplant with salt and chimichurri or oregano;","3 - Coat with cornstarch;","4 - Fry in hot oil and place on absorbent paper."],"servings":"2 servings"}}'::jsonb
WHERE id = 68;

UPDATE recipes
SET translations = '{"en-US":{"name":"Eggplant parmigiana","ingredients":["240g of peeled tomato","5g of olive oil","5g of garlic","3g of salt","140g of eggplant","35g of toasted cassava flour","40g of vegan mozzarella cheese","2g of salt","200g of vegetable oil for frying"],"preparation":["1 - Wash the eggplant, cut it into slices of approximately 1 cm and let it soak in water;","2 - Mix the salt in the cassava flour and remove the eggplant slices from the water;","3 - One by one, pass the eggplant slices on both sides through the cassava flour;","4 - Fry them in very hot oil and set aside (use 200g of oil for deep frying);","5 - Beat the tomatoes with the garlic, salt and olive oil and place in a pan over low heat until it boils;","6 - In a glass dish, place a little sauce, and place the eggplant slices on top, cover with the rest of the sauce;","7 - Grate the cheese on the thickest part of the grater and distribute it over the preparation;","8 - Bake in the oven until the cheese melts, serve hot."],"servings":"4 servings"}}'::jsonb
WHERE id = 69;

UPDATE recipes
SET translations = '{"en-US":{"name":"Zucchini steak","ingredients":["150g of zucchini","30g of onion","1g of garlic","6g of vegetable oil","2g of oregano","30g of cassava flour","3g of salt","15g of cassava flour for breading","Oil for frying"],"preparation":["1 - Sauté the zucchini in oil with the other seasonings until it becomes transparent;","2 - Add the cassava flour while the pan is still on the heat;","3 - Stir vigorously for 3 minutes so that the flour incorporates well;","4 - Let it cool and shape the burgers with oiled hands;","5 - Pass through the cassava flour to bread;","6 - Fry over medium heat and place on absorbent paper."],"servings":"5 units"}}'::jsonb
WHERE id = 70;

UPDATE recipes
SET translations = '{"en-US":{"name":"Zucchini and carrot fritters","ingredients":["130g of grated zucchini","45g of grated carrot","30g of cornstarch","20g of rice flour","50g of chopped onion","3g of garlic","10g of chopped parsley","10g of chopped chives","2g of oregano"],"preparation":["1 - In a baking dish, place the grated zucchini and mix the other ingredients, except the cornstarch and rice flour;","2 - Add the starch and flour little by little until the dough becomes consistent and firm in the spoon;","3 - Separate the dough into 10 portions of approximately 32 g;","4 - With a spoon, take the portions and place them in hot oil to fry a little at a time;","5 - Remove with a slotted spoon when they are golden;","6 - Place on absorbent paper."],"servings":"10 units"}}'::jsonb
WHERE id = 71;

UPDATE recipes
SET translations = '{"en-US":{"name":"Cassava fritters","ingredients":["220g of peeled and cooked cassava","50g of cassava flour","5g of olive oil","5g of unsalted margarine","20g of cream","3g of salt","Cassava flour for breading","Oil for frying"],"preparation":["1 - Mash the cassava while still hot with a little cooking water to make it easier;","2 - Add the cream, margarine and add the cassava flour little by little;","3 - mix until it forms a moldable dough with your hands;","4 - Make the fritters in the shape you prefer and pass them through the toasted cassava flour;","5 - Fry in hot oil quickly so they don''t open and burst."],"servings":"10 units"}}'::jsonb
WHERE id = 72;

UPDATE recipes
SET translations = '{"en-US":{"name":"Rice and carrot fritters","ingredients":["80g of cooked rice","100g of grated carrot","20g of cream","10g of parsley","10g of xanthan gum or neutral binder","3g of salt","150g of gluten-free flour","125ml of warm water","15g of onion","Oil for frying"],"preparation":["1 - In a container, place the flour and add the warm water. mix until it forms a firm dough;","2 - Add the cooked rice and the other ingredients, mix well;","3 - With the help of a spoon, portion the dough;","4 - Fry in hot oil and remove when they are golden, place on absorbent paper."],"servings":"20 units"}}'::jsonb
WHERE id = 73;

UPDATE recipes
SET translations = '{"en-US":{"name":"Rice and corn fritters","ingredients":["250g of cooked rice","30g of cornstarch","50g of carrot","30g of onion","20g of corn","4g of neutral binder","50g of cream","30g of gluten-free flour","3g of salt","1g of dried oregano","Oil for frying"],"preparation":["1 - In a container, mix the cooked rice and the other ingredients;","2 - If you prefer, put the mixture in the processor to obtain a more homogeneous dough;","3 - With the help of a spoon or with your hands, shape the fritters;","4 - Fry in hot oil until golden and place on absorbent paper."],"servings":"20 units"}}'::jsonb
WHERE id = 74;

UPDATE recipes
SET translations = '{"en-US":{"name":"Potato and eggplant fritters","ingredients":["150g of cooked mandioquinha","20g of chopped tomato","40g of eggplant","20g of chopped onion","2g of garlic","35g of cassava flour","5g of parsley","3g of salt","Oil for frying"],"preparation":["1 - Mash the mandioquinha until it forms a puree and set aside;","2 - Sauté the eggplant with the onion, garlic and tomato and add the parsley;","3 - mix the sautéed mixture with the mandioquinha puree and add the cassava flour to give it consistency;","4 - Fry in hot oil and place on absorbent paper."],"servings":"10 units"}}'::jsonb
WHERE id = 75;

UPDATE recipes
SET translations = '{"en-US":{"name":"Sweet potato fritters","ingredients":["300g of sweet potato (dough)","60g of sweet cassava flour (dough)","20g of chopped tomato (dough)","20g of chopped bell pepper (dough)","20g of chopped onion (dough)","10g of vegetable oil (dough)","90g of onion (filling)","80g of tomato (filling)","20g of cream (filling)","0,5g of oregano (filling)","40g of corn (filling)","10g of olive oil (filling)"],"preparation":["1 - Cook the sweet potato and set aside 300g (dough);","2 - In a container, mash the sweet potato until it forms a puree (dough);","3 - In a frying pan, sauté the onion, bell pepper and tomato with oil (dough);","4 - Let it cool a bit and mix with the sweet potato (dough);","5 - Add the cassava flour little by little, stirring with a spoon and then with your hands until it forms a homogeneous dough that can be shaped (dough);","6 - Divide the dough into 13 equal portions of approximately 30g (dough);","7 - Make balls and set aside (dough);","8 - In a frying pan, sauté the onion, tomato and corn with oil (filling);","9 - Add the oregano and cream, let it boil a bit (filling);","10 - Remove from heat and divide the filling into 13 equal portions (filling);","11 - In each ball of dough, make a hole in the center to place the filling and close again (filling);","12 - Place in the air fryer without oil, until they are golden (filling)."],"servings":"13 units"}}'::jsonb
WHERE id = 76;

UPDATE recipes
SET translations = '{"en-US":{"name":"Plantain ceviche","ingredients":["150g of plantain","80g of tomato","40g of onion","5g of parsley","20g of lemon juice","10g of olive oil","1g of salt","0,1g of pepper (optional)"],"preparation":["1 - Chop the plantain into thin slices and spread on a plate;","2 - Add the other chopped ingredients on top;","3 - Season with olive oil, salt, lemon juice and pepper, if you prefer."],"servings":"2 servings"}}'::jsonb
WHERE id = 77;

UPDATE recipes
SET translations = '{"en-US":{"name":"Breaded chuchu","ingredients":["170g of chuchu","30g of cornstarch","1g of salt","1g of dried oregano","Oil for frying"],"preparation":["1 - Peel the chuchu and cut into thin slices lengthwise, set aside;","2 - In a container, mix the cornstarch with salt and oregano;","3 - Pass the chuchu slices through water and then through the starch;","4 - Fry in hot oil and place on absorbent paper."],"servings":"2 servings"}}'::jsonb
