UPDATE recipes
SET translations = '{"en-US":{"name":"Vegetable Stroganoff","ingredients":["40g of onion","2g of garlic","30g of potato","30g of sweet potato","40g of zucchini","40g of carrot","40g of chuchu","50g of tomato without seeds","0,2g of dried oregano","2g of mustard","20g of ketchup","60g of cream","4g of salt","15g of olive oil","250ml of water"],"preparation":["1 - Sauté the onion and garlic in olive oil, add the chopped vegetables and oregano;","2 - Stir well and add the salt;","3 - Add the water, cover the pan and lower the heat;","4 - Cook the vegetables until they are tender;","5 - Add the mustard and ketchup;","6 - Turn off the heat, add the cream and stir gently."],"servings":"4 servings"}}'::jsonb
WHERE id = 80;

UPDATE recipes
SET translations = '{"en-US":{"name":"Farofa","ingredients":["50g of grated carrot","80g of tomato without seeds","40g of grated onion","5g of parsley","25g of chopped olives","4g of salt","15g of vegetable oil","25g of black raisins","25g of toasted cassava flour"],"preparation":["1 - In a pan, sauté the onion in oil and add the carrot and tomato;","2 - Let it wilt and add the other ingredients, except the flour;","3 - Add the flour and cook for a few minutes."],"servings":"6 servings"}}'::jsonb
WHERE id = 81;

UPDATE recipes
SET translations = '{"en-US":{"name":"Vegetable burger","ingredients":["300g of grated carrot","280g of grated zucchini","100g of grated beetroot","80g of chopped onion","12g of vegetable oil","150g of cassava flour","200ml of water","5g of salt"],"preparation":["1 - In a pan, sauté the vegetables with the onion and oil until they are tender;","2 - Add the water and cassava flour and stir to thicken over low heat;","3 - Let it cook well. Wait for it to cool and shape the burgers;","4 - Divide the dough into fifteen equal portions."],"servings":"15 units"}}'::jsonb
WHERE id = 82;

UPDATE recipes
SET translations = '{"en-US":{"name":"Eggplant burger","ingredients":["280g of eggplant","60g of onion","5g of garlic","5g of parsley","100g of gluten-free flour","15g of low-protein flour","5g of xanthan gum or neutral binder","3g of salt","0,5g of dried oregano or chimichurri","10g of vegetable oil"],"preparation":["1 - Cut the eggplant into small cubes, let it soak in salt water for two hours, drain;","2 - Sauté the onion until tender, add the garlic, eggplant and neutral binder or xanthan gum, and cook;","3 - In a food processor, blend the mixture;","4 - Add the flours and parsley;","5 - Refrigerate until completely cold;","6 - Roll out the dough between two plastics;","7 - With a glass, shape the burgers to the desired size;","8 - Grill in a pan with a drizzle of oil;","9 - It is possible to freeze, and when using, let it rest in the refrigerator for a while before grilling."],"servings":"6 units"}}'::jsonb
WHERE id = 83;

UPDATE recipes
SET translations = '{"en-US":{"name":"Cauliflower burger","ingredients":["300g of cauliflower","5g of garlic","15g of onion","3g of chives","3g of parsley","50g of gluten-free flour","3g of neutral binder","2g of salt"],"preparation":["1 - Wash and cook the cauliflower until it is very tender;","2 - Mash the cauliflower and sauté in a pan with the other seasonings;","3 - Add the neutral binder, salt and flour little by little;","4 - Stir over low heat until a drier dough forms;","5 - Let it cool and shape the burgers;","6 - Grill quickly in a pan with a drizzle of oil."],"servings":"10 units"}}'::jsonb
WHERE id = 84;

UPDATE recipes
SET translations = '{"en-US":{"name":"Eggplant kibe","ingredients":["300g of eggplant pulp","30g of onion","2g of mint","5g of salt","50g of raw cassava flour","0,2g of black pepper","8g of juice of lemon","15g of olive oil"],"preparation":["1 - Cut the eggplants lengthwise, dividing them into two parts;","2 - Arrange them in a baking dish and make a crosshatch pattern with a knife on each one;","3 - Season with black pepper, salt and olive oil;","4 - Bake until soft, approximately 40 minutes;","5 - Scrape the pulp with a spoon and weigh 300g, reserve;","6 - Mash or blend the eggplant pulp in a mixer with the other ingredients, except the flour;","7 - Add the flour until a moldable dough is obtained;","8 - Shape into kibe form;","9 - Bake in a preheated oven at 200°C until it becomes drier."],"servings":"10 units"}}'::jsonb
WHERE id = 85;

UPDATE recipes
SET translations = '{"en-US":{"name":"Creamy fried cassava","ingredients":["130g of cassava","3g of salt","5g of chives","Oil for frying"],"preparation":["1 - Cook the cassava and place it in a baking dish, mashing it a little;","2 - Let it rest in the refrigerator for 20 minutes;","3 - Cut the cassava into cubes;","4 - Fry in very hot oil and season."],"servings":"2 servings"}}'::jsonb
WHERE id = 86;

UPDATE recipes
SET translations = '{"en-US":{"name":"Vegetable sauce","ingredients":["60g of raw zucchini","20g of raw broccoli","50g of raw tomato","60g of raw carrot","10g of olive oil","2g of garlic","15g of onion","3g of salt","40ml of water for cooking"],"preparation":["1 - Chop the vegetables and set aside;","2 - Sauté the onion and garlic in the olive oil and add the vegetables and salt;","3 - Add the water and let it cook until tender;","4 - Separately, cook pasta or use already cooked rice;","5 - Use a portion of the vegetable sauce and mix with a portion of pasta or rice."],"servings":"3 servings"}}'::jsonb
WHERE id = 87;

UPDATE recipes
SET translations = '{"en-US":{"name":"Homemade tomato sauce","ingredients":["480g of tomato without skin","15g of olive oil","10g of garlic","2g of fresh basil","3g of sugar","3g of salt","0,1g of black pepper"],"preparation":["1 - cut the tomatoes in a cross on the bottom part and place them in boiling water to remove the skin;","2 - Put more than the indicated quantity, because when removing the skin it will reduce;","3 - Remove from hot water and place in ice to loosen the skin;","4 - cut in half to remove the seeds, if desired;","5 - Chop the already peeled tomatoes;","6 - Sauté in olive oil with garlic, sugar, salt and pepper;","7 - beat with a mixer and let it cook;","8 - Add the fresh basil to finish."],"servings":"3 servings"}}'::jsonb
WHERE id = 88;

UPDATE recipes
SET translations = '{"en-US":{"name":"Potato gnocchi","ingredients":["250g of cooked potato","50g of gluten-free flour","20g of mayonnaise","4g of chemical baking powder","5g of parsley","3g of salt"],"preparation":["1 - Mash the cooked potato with a fork or potato masher until you get a puree;","2 - Add the other ingredients, leaving the flour for last;","3 - Mash well until you get a smooth dough;","4 - Make balls and then rolls to cut into gnocchi shape;","5 - Cook the gnocchi in boiling water;","6 - Remove with a slotted spoon when they rise to the surface;","7 - Serve with margarine or tomato sauce and sprinkle with parsley."],"servings":"2 servings"}}'::jsonb
WHERE id = 89;

UPDATE recipes
SET translations = '{"en-US":{"name":"Three tuber gnocchi","ingredients":["80g of english potato","70g of sweet potato","100g of cassava","15g of unsalted margarine","3g of salt","50g of low-protein flour","80g of tomato","50g of onion","3g of salt (sauce)"],"preparation":["1 - Peel and cook the tubers until tender;","2 - Drain well and with the help of a fork mash until you get a puree and add the margarine;","3 - Let it cool, add the salt and flour until the dough is firm;","4 - Make small balls with the dough;","5 - Cook in a pan with boiling water and a drizzle of oil until they rise to the surface;","6 - Drain with the help of a slotted spoon;","7 - Chop the tomato into small pieces;","8 - In a pan, sauté with the onion, season and let it cook;","9 - Serve with tomato sauce."],"servings":"2 servings"}}'::jsonb
WHERE id = 90;

UPDATE recipes
SET translations = '{"en-US":{"name":"No-secret nuggets","ingredients":["150g of grated zucchini","135g of potato","100g of carrot","120g of gluten-free flour","5g of salt","18g of olive oil","55g of onion","0.5g of dried oregano","25g of cassava flour"],"preparation":["1 - Cook the carrot and potato, mash until you get a puree and set aside;","2 - Sauté the zucchini in the olive oil with the onion and season with salt and oregano;","3 - Add the potato and carrot puree to the pan;","4 - Turn off the heat and add the flour little by little, stirring constantly;","5 - Remove from the pan and place in another container to cool slightly;","6 - Shape the dough into nuggets, coat in cassava flour to bread and arrange on a baking sheet;","7 - Bake in a preheated oven at 200°C for a few minutes until golden."],"servings":"16 units"}}'::jsonb
WHERE id = 91;

UPDATE recipes
SET translations = '{"en-US":{"name":"Fake omelet","ingredients":["200ml of water","15g of cream of milk","20g of carrot","5g of vegetable oil","3g of salt","2g of chemical baking powder","70g of gluten-free flour","75g of chopped tomato","5g of parsley","1g of dried oregano","30g of chopped onion","30g of chopped olive"],"preparation":["1 - Beat all the ingredients for the dough in a blender and set aside;","2 - In a container, mix all the filling ingredients into the dough;","3 - Grease a frying pan with oil and pour in the mixture;","4 - Cook over low heat and brown on both sides."],"servings":"4 units"}}'::jsonb
WHERE id = 92;

UPDATE recipes
SET translations = '{"en-US":{"name":"Carrot pâté","ingredients":["90g of grated carrot","10g of chopped onion","1g of dried oregano","2g of parsley","2g of salt","40g of mayonnaise","20g of cream of milk","5g of mustard"],"preparation":["1 - In a container, mix all the ingredients with the help of a spoon;","2 - If you prefer a more homogeneous pâté, beat in a food processor or blender;","3 - Serve chilled."],"servings":"10 servings"}}'::jsonb
