UPDATE recipes
SET translations = '{"en-US":{"name":"Cassava pizza","ingredients":["150g of cassava","20g of unsalted margarine","2g of salt"],"preparation":["1 - Cook the cassava and mash with the help of a fork;","2 - Add the salt and margarine, mix well;","3 - Spread the mixture over a non-stick frying pan, do not make the dough too thin;","4 - Cook on both sides and serve with the filling of your choice."],"servings":"2 units"}}'::jsonb
WHERE id = 94;

UPDATE recipes
SET translations = '{"en-US":{"name":"Stuffed tomato","ingredients":["180g of tomato (3 units of 60g each)","185g of chopped palm heart","10g of chopped olive","25g of chopped mushrooms","10g of onion","5g of garlic","10g of vegetable oil","10g of parsley","2g of salt","30g of maionese","30g of cream of milk","30g of grated vegan cheese","0.5g of dried oregano"],"preparation":["1 - Wash the tomatoes and cut a lid from the top of each one and remove the seeds, set aside;","2 - Sauté the other ingredients in oil, except the parsley;","3 - Turn off the heat and add the parsley;","4 - For the creamy cheese, mix the mayonnaise, oregano, cream of milk and grated vegan cheese;","5 - Grease a baking sheet with oil or margarine and place the tomatoes;","6 - Drizzle the tomatoes inside with olive oil and salt;","7 - Place the filling inside and the cheese topping on top;","8 - Bake in a preheated oven at 220°C for approximately 25 minutes or until golden."],"servings":"3 units"}}'::jsonb
WHERE id = 95;

UPDATE recipes
SET translations = '{"en-US":{"name":"Skillet pie","ingredients":["100g of chopped tomato without seeds","20g of chopped olive","55g of chopped mushrooms","30g of grated carrot","4g of salt","1g of dried oregano","5g of  parsley","50g of vegetable oil","50g of cream of milk","210ml of water","155g of gluten-free flour","3g of salt","2g of apple cider vinegar","1g of xanthan gum or emulsifier","4g of chemical baking powder","35g of grated vegan cheese"],"preparation":["1 - mix all the ingredients of the filling and set aside;","2 - mix all the ingredients of the dough until obtaining a homogeneous mixture;","3 - Grease a non-stick frying pan with margarine and, without turning on the heat, place half of the dough;","4 - Spread the filling on top and pour the remaining dough to cover;","5 - Sprinkle vegan cheese and oregano on top;","6 - Cover the frying pan and turn on the heat very low;","7 - Cook for about 8 to 10 minutes on one side;","8 - Flip with the help of a plate and let it cook for the same time on the other side."],"servings":"10 slices"}}'::jsonb
WHERE id = 96;

UPDATE recipes
SET translations = '{"en-US":{"name":"Vegetable pie","ingredients":["180ml of water","40g of cream of milk","2g of chemical baking powder","4g of salt","150g of gluten-free flour","50g of vegetable oil","50g of zucchini","50g of carrot","60g of palm heart","65g of tomato","30g of olive","5g of parsley","30g of onion","20g of vegetable oil","4g of salt"],"preparation":["1 - beat all the ingredients of the dough in a blender or mixer and set aside;","2 - chop all the ingredients of the filling and sauté with the oil and onion;","3 - In a baking dish or refractory, pour the dough and the filling on top;","4 - Take to the oven at medium temperature until completely baked."],"servings":"10 slices"}}'::jsonb
